// src/components/UrduCalender.js
import React, { useState } from 'react';
import Calendar from 'react-calendar';
import moment from 'moment-hijri';
import 'react-calendar/dist/Calendar.css';
import './ UrduCalender.css';

const UrduCalender = () => {
  const [date, setDate] = useState(new Date());

  const onDateChange = (newDate) => {
    setDate(newDate);
  };

  const formatHijriDate = (date) => {
    return moment(date).format('iYYYY/iMM/iDD');
  };

  return (
    <div className="urdu-calendar-container">
      <Calendar onChange={onDateChange} value={date} />
      <div className="hijri-date">
        {formatHijriDate(date)}
      </div>
    </div>
  );
};

export default UrduCalender;
